contracts/Raffle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Random.sol";
import "./FundsManager.sol";
import "./interfaces/IERC721.sol";
import "./utils/Ownable.sol";

contract Raffle is Ownable {

    FundsManager public funds_manager;
    RandomNumberConsumer public random_contract;


    // Event to be emitted when a raffle is started
    event RaffleStarted(uint256 raffleId, address NFTcontract, uint256 NFTid, uint256 ticket_amount, uint256 ticket_price);

    // Event to be emitted when a player enters a raffle
    event RaffleEntered(uint256 raffleId, address player, uint256 ticket_amount);

    // Event to be emitted when a raffle is closed
    event RaffleEnded(uint256 raffleId, bool tickets_fully_sold);

    // Event to be emitted when a raffle is closed
    event RaffleWinner(uint256 raffleId, address winner);


    enum RAFFLE_STATE { CLOSED, OPEN, CALCULATING_WINNER }


    // Raffle struct to store raffle info
    struct raffle {
        address owner;                              // Owner of the raffle
        RAFFLE_STATE raffle_state;                // State of the raffle
        uint256 ticket_price;                       // Price of a ticket
        uint256 ticket_amount;                      // Amount of tickets to be sold
        uint256 ticket_sold;                        // Amount of tickets sold
        address NFTcontract;                        // NFT contract address to be won
        uint256 NFTid;                              // NFT id to be won
        address[] players;                          // Players who bought tickets
        mapping(address => uint256) player_total_paid; // Player => amount of tickets
    }

    // RaffleId => raffle
    mapping(uint256 => raffle) public lotteries;
    uint256 public raffleId;

    // Owner => RaffleId
    mapping(address => uint256) public ownerRaffleId;

    // RequestId => RaffleId
    mapping(uint256 => uint256) public requestId_to_raffleId;

    // Winning fee
    uint256 public winning_fee = 2;
    uint256 public winning_fee_divisor = 100;

    // Fee balance to be sent to the funds manager
    uint256 public fee_balance;

    // Minimum fee to be sent to the funds manager
    uint256 public min_fee_balance = 0.02 ether; // TODO : to be changed before launching


    bool canStartRaffle = true;

    modifier raffleCurrentlyStarting() {
        require(canStartRaffle, "A new raffle has been started, just wait a bit");
        canStartRaffle = false;
        _;
        canStartRaffle = true;
    }    



    constructor(address random_address, address _funds_manager_address) {
        random_contract = RandomNumberConsumer(random_address);
        funds_manager = FundsManager(_funds_manager_address);
        raffleId = 1;
    }



    /**
    * Start a new raffle (A seller of an NFT can start a new raffle)
    * @param _NFTcontract Address of the NFT contract to be won
    * @param _NFTid Id of the NFT to be won
    * @param _ticket_price Price of a ticket
    * @param _ticket_amount Amount of tickets to be sold
    */
    function start_new_raffle(address _NFTcontract, 
                               uint256 _NFTid, 
                               uint256 _ticket_price,
                               uint256 _ticket_amount) public raffleCurrentlyStarting {

        // Check if the ticket price is greater than 0
        require(_ticket_price > 0, "Ticket price can't be 0");

        // Check if the ticket amount is greater than 0
        require(_ticket_amount > 0, "Ticket amount can't be 0");

        // Check if the owner already has a raffle running                        
        require(ownerRaffleId[msg.sender] == 0, "You already have a raffle running");

        // Transfer the NFT to the contract
        // Note : Owner must approve the contract to transfer the NFT first
        IERC721(_NFTcontract).transferFrom(msg.sender, address(this), _NFTid);          

        // Set the raffleId to the owner
        ownerRaffleId[msg.sender] = raffleId;

        // Set the raffle info
        lotteries[raffleId].owner = msg.sender;
        lotteries[raffleId].ticket_price = _ticket_price;
        lotteries[raffleId].raffle_state = RAFFLE_STATE.OPEN;
        lotteries[raffleId].ticket_amount = _ticket_amount;
        lotteries[raffleId].NFTcontract = _NFTcontract;
        lotteries[raffleId].NFTid = _NFTid;

        // Increment the raffleId for the next raffle
        raffleId+=1;

        // Emit the event
        emit RaffleStarted(raffleId, _NFTcontract, _NFTid, _ticket_amount ,_ticket_price);
    }


    /**
    * Enter a raffle
    * @param _raffleId Id of the raffle to enter
    * @param _ticket_amount Amount of tickets to buy
    */
    function enter(uint256 _raffleId, uint256 _ticket_amount) public payable {

        // Requires the ticket price to be correct
        require(msg.value == _ticket_amount*lotteries[_raffleId].ticket_price, "Wrong price, check the ticket price");

        // Requires the raffle to be opened
        require(lotteries[_raffleId].raffle_state == RAFFLE_STATE.OPEN, "The raffle hasn't even started!");

        // Requires the raffle to not be fully sold
        require((lotteries[_raffleId].ticket_sold + _ticket_amount) <= lotteries[_raffleId].ticket_amount, "The raffle is full");

        // Add the address of the buyer to the raffle info
        lotteries[_raffleId].players.push(msg.sender);

        // Add the amount of tickets bought by the player
        lotteries[_raffleId].player_total_paid[msg.sender] += _ticket_amount*lotteries[_raffleId].ticket_price;

        // Increment the amount of tickets sold
        lotteries[_raffleId].ticket_sold += _ticket_amount;

        // Emit the event
        emit RaffleEntered(_raffleId, msg.sender, _ticket_amount);
    }     


    /**
    * End the raffle
    * @param _raffleId Id of the raffle to end
    */
    function end_raffle(uint256 _raffleId) public {

        // Requires the caller to be the owner of the raffle
        require(msg.sender == lotteries[_raffleId].owner, "You are not the owner of this raffle");

        // Requires the raffle to still be opened
        require(lotteries[_raffleId].raffle_state == RAFFLE_STATE.OPEN, "The raffle hasn't even started!");

        // Check if the raffle is fully sold
        bool _tickets_fully_sold;
        if (lotteries[_raffleId].ticket_sold < lotteries[_raffleId].ticket_amount) {

            // Transfer the NFT back to the owner if the raffle is not fully sold
            _tickets_fully_sold = false;
            lotteries[_raffleId].raffle_state = RAFFLE_STATE.CLOSED;
            IERC721(lotteries[_raffleId].NFTcontract).transferFrom(address(this), 
                                                                    lotteries[_raffleId].owner, 
                                                                    lotteries[_raffleId].NFTid);

            // Transfer the ticket price back to the players     
            for (uint256 i = 0; i < lotteries[_raffleId].players.length; i++) {
                address _current_player = lotteries[_raffleId].players[i];
                uint256 _total_paid = lotteries[_raffleId].player_total_paid[_current_player];
                payable(_current_player).transfer(_total_paid);
            }        

            // Reset the raffle for the owner
            ownerRaffleId[msg.sender] = 0;

        }
        else {

            // Ask ChainLink for a random number to calculate the winner if the raffle is fully sold
            _tickets_fully_sold = true;
            lotteries[_raffleId].raffle_state = RAFFLE_STATE.CALCULATING_WINNER;
            uint256 _requestId = random_contract.getRandom();

            // Set the raffleId to the requestId (so we can get the raffleId from the requestId in the callback)
            requestId_to_raffleId[_requestId] = _raffleId;
        }

        // Emit the event
        emit RaffleEnded(_raffleId, _tickets_fully_sold);
    }


    /**
    * Callback function called by ChainLink when the random number is ready
    * @param requestId Id of the request
    * @param random Random number generated by ChainLink
    */
    function raffle_result(uint256 requestId, uint256 random) external {

        // Get the raffleId from the requestId
        uint256 curr_raffle_id = requestId_to_raffleId[requestId];

        // Requires the caller to be the random contract
        require(msg.sender == address(random_contract), "Only the RandomNumberConsumer contract can call this function");

        // Requires the raffle to still be in the calculating winner stage
        require(lotteries[curr_raffle_id].raffle_state == RAFFLE_STATE.CALCULATING_WINNER, "You aren't at that stage yet!");

        // Requires the random to be greater than 0
        require(random > 0, "random-not-found");

        // Set the raffle state to closed
        lotteries[curr_raffle_id].raffle_state = RAFFLE_STATE.CLOSED;
        // Reset the owner raffleId
        ownerRaffleId[lotteries[curr_raffle_id].owner] = 0;

        // Calculate the winner
        uint256 index = random % lotteries[curr_raffle_id].players.length;
        address winner = lotteries[curr_raffle_id].players[index];

        // Transfer the NFT to the winner
        IERC721(lotteries[curr_raffle_id].NFTcontract).transferFrom(address(this), 
                                                                     winner, 
                                                                     lotteries[curr_raffle_id].NFTid);

        // Calculate the fee and the amount collected during the raffle                                                                    
        uint256 _amount_collected = lotteries[curr_raffle_id].ticket_price * lotteries[curr_raffle_id].ticket_amount;                                                                     
        uint256 _fee = (_amount_collected * winning_fee) / winning_fee_divisor;
        uint256 _owner_amount = _amount_collected - _fee;

        // Update the fee balance
        fee_balance += _fee;

        // Check if the fee balance is greater than min_fee_balance and send it to the funds manager
        if (fee_balance>min_fee_balance) {
            send_fee_balance();
        }

        // Transfer the amount collected to the owner
        payable(lotteries[curr_raffle_id].owner).transfer(_owner_amount);

        // Reset the raffle for the owner
        ownerRaffleId[msg.sender] == 0;

        // Emit the event
        emit RaffleWinner(curr_raffle_id, winner);
    }




    /**
    * Function to send the fee balance to the funds manager
    */
    function send_fee_balance() internal {
        uint256 _fee_balance = fee_balance;
        fee_balance = 0;
        funds_manager.handle_funds{value: _fee_balance}();
    }



    /** 
    * Getter to get the players of a given raffle
    * @param _raffleId Id of the raffle
    */
    function get_players(uint256 _raffleId) public view returns (address[] memory) {
        return lotteries[_raffleId].players;
    }

    /**
    * Getter to get current pot of a given raffle
    * @param _raffleId Id of the raffle
    */
    function get_pot(uint256 _raffleId) public view returns(uint256){
        return lotteries[_raffleId].ticket_price * lotteries[_raffleId].ticket_amount;
    }

    /**
    * Getter to get the state of a given raffle
    * @param _raffleId Id of the raffle
    */
    function get_raffle_state(uint256 _raffleId) public view returns(RAFFLE_STATE){
        return lotteries[_raffleId].raffle_state;
    }


    // ***************************************************************************** //
    //                               SETTERS                                         //
    // ***************************************************************************** //


    /**
    * Function to set the minimum fee balance
    * @param _min_fee_balance min fee balance
    */
    function setMinFeeBalance(uint256 _min_fee_balance) public onlyOwner {
        min_fee_balance = _min_fee_balance;
    }

}

contracts/Random.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./CoinFlip.sol";
import "./Raffle.sol";
import "./utils/VRFV2WrapperConsumerBase.sol";
import "./utils/Ownable.sol";


contract RandomNumberConsumer is VRFV2WrapperConsumerBase, Ownable {

    CoinFlip public flip_contract;
    Raffle public raffle_contract;

    mapping(uint256 => address) caller;


    mapping(uint256 => uint256) public random_numbers; 

    // Works for now
    uint32 callbackGasLimit = 2400000; // Increase in case of fund manager that needs gas to swap to link and send FTM..

    // 5 confirmations
    uint16 requestConfirmations = 3; 

    // Only one random number at a time for both CoinFlip and Raffle
    uint32 numWords = 1;    

    // Address LINK - hardcoded for Fantom testnet (but can be changed with the setter function)
    address linkAddress = 0xfaFedb041c0DD4fA2Dc0d87a6B0979Ee6FA7af5F;

    // address WRAPPER - hardcoded for Fantom testnet (but can be changed with the setter function)
    address wrapperAddress = 0x38336BDaE79747a1d2c4e6C67BBF382244287ca6;    

    // Counter to make sure the set_contracts can only be called once
    uint256 public only_once_counter = 0;

    modifier onlyOnce() {
        require(only_once_counter == 0, "Can only be called once");
        _;
    }



    constructor() VRFV2WrapperConsumerBase(linkAddress, wrapperAddress) {}


    /** 
     * Requests randomness from a user-provided seed
     * @dev This function is called by the CoinFlip/Raffle contract to request randomness
     */
    function getRandom() external returns (uint256) {
        require(msg.sender == address(flip_contract) || 
                msg.sender == address(raffle_contract),
                     "Only the CoinFlip/Raffle contract can call this function");

        // Request randomness from the VRF_V2_WRAPPER contract             
        uint256 _requestId = requestRandomness(callbackGasLimit, requestConfirmations, numWords);

        // Store the request ID in the mapping
        random_numbers[_requestId] = 0; 

        // Store the caller in the mapping
        caller[_requestId] = msg.sender;

        // Return the request ID for further use
        return _requestId;       
    }



    /**
     * Callback function used by VRF Coordinator
     * @dev This function is called by the VRF Coordinator to fulfill the requestRandomness request
    * @param _requestId The ID of the request to fulfill
    * @param _randomWords The random result returned by the oracle
     */
    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        require(msg.sender == address(VRF_V2_WRAPPER), "Only VRF_V2_WRAPPER can fulfill");
        random_numbers[_requestId] = _randomWords[0];

        // Call the raffle_result/flip_result function of the CoinFlip/Raffle contract
        address caller_address = caller[_requestId];
        if (caller_address == address(raffle_contract)){

            raffle_contract.raffle_result(_requestId, _randomWords[0]);
        }
        else if (caller_address == address(flip_contract)) {

            flip_contract.flip_result(_requestId, _randomWords[0]);
        }
        else {
            revert("Caller not recognized");
        }
    }



    // ***************************************************************************** //
    //                               SETTERS                                         //
    // ***************************************************************************** //


    /**
    * @dev Sets the callback gas limit
    * @param gas_limit The gas limit for the callback
    */
    function set_callback_gas_limit(uint32 gas_limit) public onlyOwner{
        callbackGasLimit = gas_limit;
    }

    /**
     * @dev Sets the number of request confirmations for a random number request
     * @param confirmations The number of confirmations
     */
    function set_request_confirmations(uint16 confirmations) public onlyOwner{
        requestConfirmations = confirmations;
    }

    /**
     * @dev Sets the address of the LINK token
     * @param link_address The address of the LINK token
     */
    function set_link_address(address link_address) public onlyOwner{
        linkAddress = link_address;
    }

    /**
     * @dev Sets the address of the VRF_V2_WRAPPER contract
     * @param wrapper_address The address of the VRF_V2_WRAPPER contract
     */
    function set_wrapper_address(address wrapper_address) public onlyOwner{
        wrapperAddress = wrapper_address;
    }


    /**
     * @dev Sets the address of the CoinFlip and Raffle contracts
     * @param flip_address The address of the CoinFlip contract (payable because of it can receive FTM)
     * @param raffle_address The address of the Raffle contract
     */
    function set_contracts(address payable flip_address, address raffle_address) public onlyOnce onlyOwner{
        flip_contract = CoinFlip(flip_address);
        raffle_contract = Raffle(raffle_address);
        only_once_counter += 1;
    }


}

contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./utils/ERC1155Holder.sol";
import "./interfaces/IERC1155.sol";
import "./utils/Ownable.sol";

contract Staking is ERC1155Holder, Ownable{


    IERC1155 public immutable stakingToken;

    event FTMReceived(address indexed sender, uint256 amount);
    event NFTStaked(address indexed sender, uint256 amount);
    event NFTUnstaked(address indexed sender, uint256 amount);
    event RewardsClaimed(address indexed sender, uint256 amount);


    // Total staked (store total amount of tokens staked by all users)
    uint public totalStaked;  


    // The default duration of staking is 1 day
    uint256 public staking_duration = 1 days;

    // The struct StakingInfo stores the start time and reward rate of the staking
    struct StakingInfo{
        uint256 start;
        uint256 reward_rate;
    }

    StakingInfo public staking_info; // Need to initialize it ?

    // User address => rewards to be claimed
    mapping(address => uint) public rewards;

    // User address => staked amount
    mapping(address => uint) public balanceOf; 

    // Every time a user stakes/unstakes or claims rewards, the reward per token is updated
    // This mapping keeps track of the reward per token already taken into account for each user
    mapping(address => uint256) public userRewardPerTokenAccounted;    

    // updatedAt keeps track of the last time the reward per token was updated
    uint256 public updatedAt;

    // accumulatedRewardPerNFT keeps track of the total sum of reward per token
    uint256 public accumulatedRewardPerNFT;



    constructor(address _stakingNFT) {
        stakingToken = IERC1155(_stakingNFT);
    }


    // Modifier called every time a user stakes/unstakes or claims rewards
    // It basically updates the user's rewards info
    modifier updateRewards(address _account) {

        // First we update the accumulatedRewardPerNFT 
        // This changes the reward per token for all users
        accumulatedRewardPerNFT = getRewardPerToken();

        // Then set updateAt to the current time
        // For next time the modifier is called
        updatedAt = block.timestamp;


        // We update the rewards for the user
        rewards[_account] = getRewards(_account);
        userRewardPerTokenAccounted[_account] = accumulatedRewardPerNFT;

        _;

    }  




    /**
     * Main external function for user to stake tokens
     * @notice - user must approve the contract to transfer tokens before calling this function
     * @param _amount - amount of tokens to stake
     */
    function stake(uint _amount) external updateRewards(msg.sender) {
        require(_amount > 0, "amount = 0");

        // Transfers token from user to the contract
        stakingToken.safeTransferFrom(msg.sender, address(this), 1, _amount, "");

        // Updates total staked amount
        totalStaked += _amount;

        // Updates user's balance
        balanceOf[msg.sender] += _amount;


        emit NFTStaked(msg.sender, _amount);
    }

    /**
     * This function allows a user to withdraw a certain amount of tokens staked in the contract.
     * @param _amount - amount of tokens to withdraw
     */
    function unstake(uint _amount) external updateRewards(msg.sender) {  

        require(_amount > 0, "amount = 0"); 

        // Updates user's balance
        balanceOf[msg.sender] -= _amount;

        // Updates total staked amount
        totalStaked -= _amount;

        // Transfers token from contract to user
        stakingToken.safeTransferFrom(address(this), msg.sender, 1, _amount, "");

        emit NFTUnstaked(msg.sender, _amount);
    }


    /**
     * Allows user to claim rewards
     */
     function claimRewards() public updateRewards(msg.sender) {

        // Get user current reward (Note that this reward is updated via the modifier)
        uint reward = rewards[msg.sender];
        require(reward > 0, "No rewards to be claimed"); 

        // Resets user rewards
        rewards[msg.sender] = 0;

        // Transfers rewards to user
        payable(msg.sender).transfer(reward);        

        // Emits the associated event
        emit RewardsClaimed(msg.sender, reward);
    }




    /**
     * Allows user to claim rewards
     * @param _account - address of particular user
     * @notice - The rewards depends on current contract's balance, sometimes it worth waiting for a while
     * to claim rewards :) 
     * @return rewards - total rewards for user
     */
     function getRewards(address _account) public view returns (uint256) {

        // Get user reward (that was updated the last time he interacted with this contract)
        uint256 _user_total_reward = rewards[_account];

        // Get user accumulated reward with past and current reward rates
        uint256 _user_accumulated_reward = balanceOf[_account] * getRewardPerToken();

        // Get user reward already taken into account (that was updated the last time he interacted with this contract)
        uint256 _user_accounted_reward = balanceOf[_account] * userRewardPerTokenAccounted[_account];


        return _user_total_reward + _user_accumulated_reward - _user_accounted_reward; 

    }    


    /**
    * This function updates the reward per token 
    * It basically adds past rewards per token rate to the current one
    */
    function getRewardPerToken() public view returns (uint) {
        if (totalStaked == 0) {
            return accumulatedRewardPerNFT;
        }

        // Get current reward per token rate
        // This is calculated by multiplying the reward rate by the duration since the last update
        // and dividing by the total staked amount (in order to get the reward per token)
        uint256 _current_reward_per_token = 
                        (staking_info.reward_rate * (block.timestamp - updatedAt)) / totalStaked;

        return accumulatedRewardPerNFT + _current_reward_per_token;            
    }




    /**
     * Allows owner to withdraw all funds from the contract
     * @notice - This function is only for testing (not wasting faucet funds)
     */
    function emergencyWithdraw() external onlyOwner{
        payable(owner()).transfer(address(this).balance);
    }



    /**
    * This function is called by the fallback function
    * It checks if the staking period is over and if so, it calculates the reward rate
    * and updates the staking info
    */
    function manage_new_funds() public payable { // TODO : needs a require statement
        uint256 time_since_staking_started = block.timestamp - staking_info.start;

        // Note : We need a minimum amount for staking
        if (time_since_staking_started > staking_duration) {// && address(this).balance > 0.5 ether) {

            // If the period is over, calculate the reward rate
            staking_info.reward_rate = address(this).balance / staking_duration;
            staking_info.start = block.timestamp;

        }

    }



    // ***************************************************************************** //
    //                               SETTERS                                         //
    // ***************************************************************************** //



    /**
     * Allows owner to set the staking duration
     * @param _duration - new staking duration
     */
     function setStakingDuration(uint256 _duration) external onlyOwner {
        staking_duration = _duration;
    }



}

