contracts/coin_flip.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./randomness.sol";

contract CoinFlip {

    RandomNumberConsumer public randomness_contract;

    event FTMReceived(address sender, uint256 amount);
    event GamePlay(address player, uint256 amount, uint256 bet, uint256 request_id);
    event GameResult(address player, uint256 random_number, uint256 bet);

    // 3 possible amounts for a bet
    uint256[] public amounts = [0.1 ether, 0.5 ether, 1 ether]; // TODO : to be changed before launching

    // Minimum balance factor for the contract to accept new bets
    uint256 public min_balance_factor = 3; 

    // 5 percent is taken from every winning bet
    // 50/1000 = 5%
    // Note : with 1000 as denominator, the fee can have a decimal (i.e 4.5%)
    uint256 public winning_fee = 50; 
    uint256 public winning_fee_divisor = 1000;

    // 0 = heads, 1 = tails
    enum Bet {HEADS, TAILS}

    // Struct to store game data
    struct Game {
        address player;
        uint256 bet;
        uint256 amount;
        bool ended;
        bool won;
    }

    // Mapping to store games
    mapping(uint256 => Game) public games;

    // Mapping to store request ids
    mapping(address => uint256) public request_ids;

    // Mapping to check if the player is currently playing a game
    mapping(address => bool) isPlaying;

    // Fee balance to be sent to the funds manager
    uint256 public fee_balance;

    // Address of the funds manager
    address public funds_manager;

    // Minimum fee to be sent to the funds manager
    uint256 public min_fee = 0.1 ether; // TODO : to be changed before launching

    // Owner of the contract
    address public owner;


    // Modifier to check if the caller is the owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function.");
        _;
    }


    /**
        * @dev Constructor
        * @param _randomness_address Address of the RandomNumberConsumer contract
        * @param _funds_manager Address of the funds manager contract
    */
    constructor(address _randomness_address, address _funds_manager) {
        randomness_contract = RandomNumberConsumer(_randomness_address);
        funds_manager = _funds_manager;
        owner = msg.sender;
    }

    /**
    * @dev Fallback function to receive FTM
    */
    fallback() external payable {
        emit FTMReceived(msg.sender, msg.value);
    }

    /**
    * @dev Receive function to receive FTM
    */    
    receive() external payable {
        emit FTMReceived(msg.sender, msg.value);
    }

    /**
    * @dev Function to play a game
    * @param _bet 0 = heads, 1 = tails
    */
    function play(uint256 _bet) public payable {

        // Check if the bet is valid
        require(_bet == uint256(Bet.HEADS) || _bet == uint256(Bet.TAILS), 
                "You must guess either heads or tails");

        // Check if the amount sent is valid
        require(msg.value == amounts[0] || msg.value == amounts[1] || msg.value == amounts[2], 
                "Amount sent not correct");

        // Check if the player is not already playing a game
        require(!isPlaying[msg.sender], "You are already playing a game, wait for the outcome");

        // Check if the contract has enough funds to pay the winner (better revert if not)
        require(address(this).balance >= (min_balance_factor * msg.value), "Contract balance too low");         


        // Call the getRandom function of the RandomNumberConsumer contract                                  
        uint256 requestId = randomness_contract.getRandom();

        // Store the game data
        games[requestId] = Game(msg.sender,
                                _bet, 
                                msg.value, 
                                false,
                                false); 

        // Store the request id                                                                
        request_ids[msg.sender] = requestId; 

        // Set the player as playing
        isPlaying[msg.sender] = true;


        // Emit the GamePlay event
        emit GamePlay(msg.sender, msg.value, _bet, requestId);

    }

    function flip_result(uint256 requestId, uint256 random_number) public {

        // Check if the caller is the RandomNumberConsumer contract
        require(msg.sender == address(randomness_contract), 
                            "Only the RandomNumberConsumer contract can call this function");

        // Check if the game has not already ended
        require(games[requestId].ended == false, "Game already ended");

        // Check if the game exists
        require(games[requestId].player != address(0), "No game found with this id");

        // Set the game as ended
        games[requestId].ended = true;

        // Set the request id to 0 for the player
        isPlaying[games[requestId].player] = false;        

        // Get the side of the coin
        uint256 side = random_number % 2;

        // Check if the player won
        if (side == games[requestId].bet) {

            // Calculate the fee
            uint256 _fee = games[requestId].amount * winning_fee / winning_fee_divisor;

            // Update the fee balance
            fee_balance += _fee;

            // Check if the fee balance is greater than the minimum fee and send it to the funds manager
            if (fee_balance>=min_fee) {
                send_fee_balance();
            }

            // Calculate the amount won
            uint256 amount_won = games[requestId].amount * 2 - _fee;

            // Send the amount won to the player
            payable(games[requestId].player).transfer(amount_won);

            // Set the game as won
            games[requestId].won = true;

        }

        // Emit the GameResult event
        emit GameResult(games[requestId].player, side, games[requestId].bet);
    }

    /**
    * @dev Function to send the fee balance to the funds manager
    */
    function send_fee_balance() internal {
        uint256 _fee_balance = fee_balance;
        fee_balance = 0;
        payable(funds_manager).transfer(_fee_balance);
    }

    // Tmp function to withdraw funds TODO : remove before launching
    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    /**
    * @dev Function to set the address of the RandomNumberConsumer contract
    */
    function setRandomnessContract(address randomness_address) public onlyOwner {
        randomness_contract = RandomNumberConsumer(randomness_address);
    }

    /**
    * @dev Function to set the address of the funds manager
    */
    function setFundsManager(address _funds_manager) public onlyOwner {
        funds_manager = _funds_manager;
    }

    /**
    * @dev Function to set the winning fee
    */
    function setWinningFee(uint256 _winning_fee) public onlyOwner {
        winning_fee = _winning_fee;
    }

    /**
    * @dev Function to set the bet amounts
    */
    function setAmounts(uint256[] memory _amounts) public onlyOwner {
        require(_amounts.length == 3, "Array must have 3 elements");
        amounts = _amounts;
    }

    /**
    * @dev Function to set the minimum fee
    */
    function setMinFee(uint256 _min_fee) public onlyOwner {
        min_fee = _min_fee;
    }



}

contracts/lottery.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./randomness.sol";
import "../node_modules/@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "hardhat/console.sol";

contract Lottery  {

    // TODO : set a maximum ticket amount per tx

    event LotteryStarted(uint256 lotteryId, address NFTcontract, uint256 NFTid, uint256 ticket_amount, uint256 ticket_price);
    event LotteryEntered(uint256 lotteryId, address player, uint256 ticket_amount);
    event LotteryEnded(uint256 lotteryId, bool tickets_fully_sold);
    event LotteryWinner(uint256 lotteryId, address winner);

    enum LOTTERY_STATE { CLOSED, OPEN, CALCULATING_WINNER }

    RandomNumberConsumer public randomness_contract;

    // Address of the owner
    address public owner;

    // Address of the funds manager
    address public funds_manager;

    // Lottery struct to store lottery info
    struct lottery {
        address owner;                              // Owner of the lottery
        LOTTERY_STATE lottery_state;                // State of the lottery
        uint256 ticket_price;                       // Price of a ticket
        uint256 ticket_amount;                      // Amount of tickets to be sold
        uint256 ticket_sold;                        // Amount of tickets sold
        address NFTcontract;                        // NFT contract address to be won
        uint256 NFTid;                              // NFT id to be won
        address[] players;                          // Players who bought tickets
        mapping(address => uint256) player_total_paid; // Player => amount of tickets
    }

    // LotteryId => lottery
    mapping(uint256 => lottery) public lotteries;
    uint256 public lotteryId;

    // Owner => LotteryId
    mapping(address => uint256) public ownerLotteryId;

    // RequestId => LotteryId
    mapping(uint256 => uint256) public requestId_to_lotteryId;

    // Winning fee
    uint256 public winning_fee = 2;
    uint256 public winning_fee_divisor = 100;

    // Fee balance to be sent to the funds manager
    uint256 public fee_balance;

    bool canStartLottery = true;

    modifier lotteryCurrentlyStarting() {
        require(canStartLottery, "A new lottery has been started, wait just a bit");
        canStartLottery = false;
        _;
        canStartLottery = true;
    }    

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function.");
        _;
    }


    constructor(address randomness_address, address _funds_manager) {
        owner = msg.sender;
        randomness_contract = RandomNumberConsumer(randomness_address);
        funds_manager = _funds_manager;
        lotteryId = 1;
    }



    /**
    * @dev Start a new lottery (A seller of an NFT can start a new lottery)
    * @param _NFTcontract Address of the NFT contract to be won
    * @param _NFTid Id of the NFT to be won
    * @param _ticket_price Price of a ticket
    * @param _ticket_amount Amount of tickets to be sold
    */
    function start_new_lottery(address _NFTcontract, 
                               uint256 _NFTid, 
                               uint256 _ticket_price,
                               uint256 _ticket_amount) public lotteryCurrentlyStarting {

        // Check if the ticket price is greater than 0
        require(_ticket_price > 0, "Ticket price can't be 0");

        // Check if the ticket amount is greater than 0
        require(_ticket_amount > 0, "Ticket amount can't be 0");

        // Check if the owner already has a lottery running                        
        require(ownerLotteryId[msg.sender] == 0, "You already have a lottery running");

        // Transfer the NFT to the contract
        // Note : Owner must approve the contract to transfer the NFT first
        IERC721(_NFTcontract).transferFrom(msg.sender, address(this), _NFTid);          

        // Set the lotteryId to the owner
        ownerLotteryId[msg.sender] = lotteryId;

        // Set the lottery info
        lotteries[lotteryId].owner = msg.sender;
        lotteries[lotteryId].ticket_price = _ticket_price;
        lotteries[lotteryId].lottery_state = LOTTERY_STATE.OPEN;
        lotteries[lotteryId].ticket_amount = _ticket_amount;
        lotteries[lotteryId].NFTcontract = _NFTcontract;
        lotteries[lotteryId].NFTid = _NFTid;

        // Increment the lotteryId for the next lottery
        lotteryId+=1;

        // Emit the event
        emit LotteryStarted(lotteryId, _NFTcontract, _NFTid, _ticket_amount ,_ticket_price);
    }


    /**
    * @dev Enter a lottery
    * @param _lotteryId Id of the lottery to enter
    * @param _ticket_amount Amount of tickets to buy
    */
    function enter(uint256 _lotteryId, uint256 _ticket_amount) public payable {

        // Requires the ticket price to be correct
        require(msg.value == _ticket_amount*lotteries[_lotteryId].ticket_price, "Wrong price, check the ticket price");

        // Requires the lottery to be opened
        require(lotteries[_lotteryId].lottery_state == LOTTERY_STATE.OPEN, "The lottery hasn't even started!");

        // Requires the lottery to not be fully sold
        require((lotteries[_lotteryId].ticket_sold + _ticket_amount) <= lotteries[_lotteryId].ticket_amount, "The lottery is full");

        // Add the address of the buyer to the lottery info
        lotteries[_lotteryId].players.push(msg.sender);

        // Add the amount of tickets bought by the player
        lotteries[_lotteryId].player_total_paid[msg.sender] += _ticket_amount*lotteries[_lotteryId].ticket_price;

        // Increment the amount of tickets sold
        lotteries[_lotteryId].ticket_sold += _ticket_amount;

        // Emit the event
        emit LotteryEntered(_lotteryId, msg.sender, _ticket_amount);
    }     


    /**
    * @dev End the lottery
    * @param _lotteryId Id of the lottery to end
    */
    function end_lottery(uint256 _lotteryId) public {

        // Requires the caller to be the owner of the lottery
        require(msg.sender == lotteries[_lotteryId].owner, "You are not the owner of this lottery");

        // Requires the lottery to still be opened
        require(lotteries[_lotteryId].lottery_state == LOTTERY_STATE.OPEN, "The lottery hasn't even started!");

        // Check if the lottery is fully sold
        bool _tickets_fully_sold;
        if (lotteries[_lotteryId].ticket_sold < lotteries[_lotteryId].ticket_amount) {

            // Transfer the NFT back to the owner if the lottery is not fully sold
            _tickets_fully_sold = false;
            lotteries[_lotteryId].lottery_state = LOTTERY_STATE.CLOSED;
            IERC721(lotteries[_lotteryId].NFTcontract).transferFrom(address(this), 
                                                                    lotteries[_lotteryId].owner, 
                                                                    lotteries[_lotteryId].NFTid);

            // Transfer the ticket price back to the players     
            for (uint256 i = 0; i < lotteries[_lotteryId].players.length; i++) {
                address _current_player = lotteries[_lotteryId].players[i];
                uint256 _total_paid = lotteries[_lotteryId].player_total_paid[_current_player];
                payable(_current_player).transfer(_total_paid);
            }        

            // Reset the lottery for the owner
            ownerLotteryId[msg.sender] = 0;

        }
        else {

            console.log("Calculating winner");
            // Ask ChainLink for a random number to calculate the winner if the lottery is fully sold
            _tickets_fully_sold = true;
            lotteries[_lotteryId].lottery_state = LOTTERY_STATE.CALCULATING_WINNER;
            uint256 _requestId = randomness_contract.getRandom();

            // Set the lotteryId to the requestId (so we can get the lotteryId from the requestId in the callback)
            requestId_to_lotteryId[_requestId] = _lotteryId;
        }

        // Emit the event
        emit LotteryEnded(_lotteryId, _tickets_fully_sold);
    }


    /**
    * @dev Callback function called by ChainLink when the random number is ready
    * @param requestId Id of the request
    * @param randomness Random number generated by ChainLink
    */
    function lottery_result(uint256 requestId, uint256 randomness) external {

        // Get the lotteryId from the requestId
        uint256 curr_lottery_id = requestId_to_lotteryId[requestId];

        // Requires the caller to be the randomness contract
        require(msg.sender == address(randomness_contract), "Only the RandomNumberConsumer contract can call this function");

        // Requires the lottery to still be in the calculating winner stage
        require(lotteries[curr_lottery_id].lottery_state == LOTTERY_STATE.CALCULATING_WINNER, "You aren't at that stage yet!");

        // Requires the randomness to be greater than 0
        require(randomness > 0, "random-not-found");

        // Set the lottery state to closed
        lotteries[curr_lottery_id].lottery_state = LOTTERY_STATE.CLOSED;
        // Reset the owner lotteryId
        ownerLotteryId[lotteries[curr_lottery_id].owner] = 0;

        // Calculate the winner
        uint256 index = randomness % lotteries[curr_lottery_id].players.length;
        address winner = lotteries[curr_lottery_id].players[index];

        // Transfer the NFT to the winner
        IERC721(lotteries[curr_lottery_id].NFTcontract).transferFrom(address(this), 
                                                                     winner, 
                                                                     lotteries[curr_lottery_id].NFTid);

        // Calculate the fee and the amount collected during the lottery                                                                    
        uint256 _amount_collected = lotteries[curr_lottery_id].ticket_price * lotteries[curr_lottery_id].ticket_amount;                                                                     
        uint256 _fee = (_amount_collected * winning_fee) / winning_fee_divisor;
        uint256 _owner_amount = _amount_collected - _fee;

        // Update the fee balance
        fee_balance += _fee;

        // Check if the fee balance is greater than 1 FTM and send it to the funds manager
        if (fee_balance>1 ether) {
            send_fee_balance();
        }

        // Transfer the amount collected to the owner
        payable(lotteries[curr_lottery_id].owner).transfer(_owner_amount);

        // Reset the lottery for the owner
        ownerLotteryId[msg.sender] == 0;

        // Emit the event
        emit LotteryWinner(curr_lottery_id, winner);
    }


    function emergency_withdraw() public {
        require(msg.sender == owner, "You are not the owner");

        console.log("Emergency withdraw");
        console.log("Balance: %s", address(this).balance);
        console.log("NFT contract : %s", lotteries[lotteryId].NFTcontract);
        console.log("NFT id : %s", lotteries[lotteryId].NFTid);
        console.log("Owner : %s", lotteries[lotteryId].owner);        
        payable(owner).transfer(address(this).balance);


        IERC721(lotteries[lotteryId].NFTcontract).transferFrom(address(this), 
                                                               lotteries[lotteryId].owner, 
                                                               lotteries[lotteryId].NFTid);        

    }


    /**
    * @dev Function to send the fee balance to the funds manager
    */
    function send_fee_balance() internal {
        uint256 _fee_balance = fee_balance;
        fee_balance = 0;
        payable(funds_manager).transfer(_fee_balance);
    }

    /** 
    * @dev Getter to get the players of a given lottery
    * @param _lotteryId Id of the lottery
    */
    function get_players(uint256 _lotteryId) public view returns (address[] memory) {
        return lotteries[_lotteryId].players;
    }

    /**
    * @dev Getter to get current pot of a given lottery
    * @param _lotteryId Id of the lottery
    */
    function get_pot(uint256 _lotteryId) public view returns(uint256){
        return lotteries[_lotteryId].ticket_price * lotteries[_lotteryId].ticket_amount;
    }

    /**
    * @dev Getter to get the state of a given lottery
    * @param _lotteryId Id of the lottery
    */
    function get_lottery_state(uint256 _lotteryId) public view returns(LOTTERY_STATE){
        return lotteries[_lotteryId].lottery_state;
    }


}

contracts/randomness.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../node_modules/@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";
import "./coin_flip.sol";
import "./lottery.sol";

contract RandomNumberConsumer is VRFV2WrapperConsumerBase {

    CoinFlip public flip_contract;
    Lottery public lottery_contract;

    mapping(uint256 => address) caller;


    mapping(uint256 => uint256) public random_numbers; 

    uint256 public counter = 0;
    address public owner;

    // Works for now
    uint32 callbackGasLimit = 1000000;

    // 5 confirmations
    uint16 requestConfirmations = 3; 

    // Only one random number at a time for both CoinFlip and Lottery
    uint32 numWords = 1;    

    // Address LINK - hardcoded for Fantom testnet (but can be changed with the setter function)
    address linkAddress = 0xfaFedb041c0DD4fA2Dc0d87a6B0979Ee6FA7af5F;

    // address WRAPPER - hardcoded for Fantom testnet (but can be changed with the setter function)
    address wrapperAddress = 0x38336BDaE79747a1d2c4e6C67BBF382244287ca6;    

    modifier onlyOnce() {
        require(counter == 0, "Can only be called once");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function.");
        _;
    }


    constructor() VRFV2WrapperConsumerBase(linkAddress, wrapperAddress) {
        owner = msg.sender;
    }

    /**
    * @dev Sets the callback gas limit
    * @param gas_limit The gas limit for the callback
    */
    function set_callback_gas_limit(uint32 gas_limit) public onlyOwner{
        callbackGasLimit = gas_limit;
    }

    /**
     * @dev Sets the number of request confirmations for a random number request
     * @param confirmations The number of confirmations
     */
    function set_request_confirmations(uint16 confirmations) public onlyOwner{
        requestConfirmations = confirmations;
    }

    /**
     * @dev Sets the address of the LINK token
     * @param link_address The address of the LINK token
     */
    function set_link_address(address link_address) public onlyOwner{
        linkAddress = link_address;
    }

    /**
     * @dev Sets the address of the VRF_V2_WRAPPER contract
     * @param wrapper_address The address of the VRF_V2_WRAPPER contract
     */
    function set_wrapper_address(address wrapper_address) public onlyOwner{
        wrapperAddress = wrapper_address;
    }


    /**
     * @dev Sets the address of the CoinFlip contract
     * @param flip_address The address of the CoinFlip contract (payable because of it can receive FTM)
     * @param lottery_address The address of the Lottery contract
     */
    function set_contracts(address payable flip_address, address lottery_address) public onlyOnce onlyOwner{
        flip_contract = CoinFlip(flip_address);
        lottery_contract = Lottery(lottery_address);
        counter = counter + 1;
    }

    /** 
     * Requests randomness from a user-provided seed
     * @dev This function is called by the CoinFlip/Lottery contract to request randomness
     */
    function getRandom() external returns (uint256) {
        require(msg.sender == address(flip_contract) || 
                msg.sender == address(lottery_contract),
                     "Only the CoinFlip/Lottery contract can call this function");

        // Request randomness from the VRF_V2_WRAPPER contract             
        uint256 _requestId = requestRandomness(callbackGasLimit, requestConfirmations, numWords);

        // Store the request ID in the mapping
        random_numbers[_requestId] = 0; 

        // Store the caller in the mapping
        caller[_requestId] = msg.sender;

        // Return the request ID for further use
        return _requestId;       
    }



    /**
     * Callback function used by VRF Coordinator
     * @dev This function is called by the VRF Coordinator to fulfill the requestRandomness request
    * @param _requestId The ID of the request to fulfill
    * @param _randomWords The random result returned by the oracle
     */
    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        require(msg.sender == address(VRF_V2_WRAPPER), "Only VRF_V2_WRAPPER can fulfill");
        random_numbers[_requestId] = _randomWords[0];

        // Call the lottery_result/flip_result function of the CoinFlip/Lottery contract
        address caller_address = caller[_requestId];
        if (caller_address == address(lottery_contract)){

            lottery_contract.lottery_result(_requestId, _randomWords[0]);
        }
        else if (caller_address == address(flip_contract)) {

            flip_contract.flip_result(_requestId, _randomWords[0]);
        }
        else {
            revert("Caller not recognized");
        }
    }


}

